from rest_framework import serializersfrom apps.accounts.models import Accountclass UserRegisterSerializer(serializers.Serializer):    """       Serializer for user registration.       Contains:       - phone: User's phone number.       """    phone = serializers.CharField(default="998901234567")class UpdateAccountModelSerializer(serializers.ModelSerializer):    """        Serializer for updating user account details.        Includes fields:        - first_name: User's first name.        - last_name: User's last name.        - bio: User's bio.        - username: User's username.        """    class Meta:        model = Account        fields = ["first_name", "last_name", "bio", "username"]class SmsPhoneSerializer(serializers.Serializer):    phone = serializers.CharField(max_length=16)