from super_app.requests_control.requests import acciodef get_wallet_balance(wallet):    payload = {        "id": "{{$randomUUID}}",        "method": "wallet.info",        "params": {            "number": wallet.card_number,            "expire": wallet.expire        }    }    try:        data = accio(payload=payload, service_name="ucoin")        if data['status']:            response = data            balance = round(data['result']['balance'] / 100, 1)            response['result']['balance'] = balance            return response    except:        data = None    return data