import timeimport requestsfrom decouple import configfrom .logger import LoggerWALLET_TOKEN = config('WALLET_TOKEN')WALLET_URL = config('WALLET_URL')def accio(payload, service_name, url=None, data=None, header=None):    logger = Logger(service_name, False, 10)    start_time = time.time()    headers = ''    if service_name == 'ucoin':        url = WALLET_URL        token = WALLET_TOKEN        headers = {            'token': token,            'Content-Type': 'application/json; charset=utf-8'        }    else:        response = {            "error": {                'code': 404,                'message': "Service not found!"            },            "status": False        }        logger.api_service_request(request=payload, service=service_name,                                   response=response, start_time=start_time)        return response    try:        response = requests.post(url=url, json=payload, headers=headers, data=data)        response = response.json()    except:        response = {            "error": {                'code': 455,                'message': "Something went wrong!"            },            "status": False        }    logger.api_service_request(request=payload, service=service_name,                               response=response, start_time=start_time)    return response